import React, { useState } from 'react'
import { Text, View, StyleSheet, TextInput, TouchableOpacity, Image } from 'react-native'
import GraphqlService from '../../functions/axios'
const service = new GraphqlService()

const Login = ({ verifyAccessToken }) => {
    const [botao, setBotao] = useState()
    const [email, setEmail] = useState()
    const [senha, setSenha] = useState()


    function validation() {
        if (email && usuario) {
            service.checkUser()
            alert(`email: ${email}  usuario ${usuario}`)
        } else {
            alert('Preencher todos os campos')
        }

    }
    function validationToRegister() {
        verifyAccessToken()

    }
    return (
        <View>
            <Image style={style.image} source={{ uri: "https://repository-images.githubusercontent.com/78664391/f7e46780-6bf6-11eb-999f-8212c69d76bc" }} />
            <Text>E-mail</Text>
            <TextInput style={style.input} id="1" onChangeText={setEmail} ></TextInput>
            <Text>Senha</Text>
            <TextInput style={style.input} id="3" onChangeText={setSenha}></TextInput>
            <TouchableOpacity style={style.btn} onPress={() => validation()} ><Text style={{ color: "white" }}>Go</Text></TouchableOpacity>
            <TouchableOpacity style={style.btn} onPress={() => validationToRegister()} ><Text style={{ color: "white" }}>Register</Text></TouchableOpacity>

        </View>

    )
}

const style = StyleSheet.create({
    btn: {
        backgroundColor: "red",
        color: "#FFFFFF",
        fontFamily: "arial",
        textAlign: "center",
        marginTop: 10,
        borderRadius: 10,
        border: "1px solid black"
    },
    input: {
        border: "1px solid black",
        borderRadius: "5%",
        borderRadius: 10
    },
    image: {
        alignSelf: "center",
        width: 120,
        height: 100
    }
})

export default Login
