import React, { useEffect, useState } from 'react'
import { Text, View, Button, FlatList, StyleSheet, ScrollView } from 'react-native'

import Users from '../../functions/database/usuarios'
const MainPage = ({ navigation }) => {

    const [fetchData, setFetchData] = useState([]);
    async function showAllUsers() {
        let usersList = await Users.all()
        console.log(usersList)
        setFetchData(usersList)
    }

    function renderItem(item){
        return <Text>{item.item}</Text>
    }

    return (
        <View>
            <Button onPress={() => showAllUsers()} title="Usuarios Cadastrados Na Base"></Button>
            {fetchData.length>0 && 
             <FlatList style={style.items} data={fetchData} renderItem={(item)=>renderItem(item) } ></FlatList>
             }
        </View>
    )
}

const style = StyleSheet.create({
    items:{
        flex: 1,
        backgroundColor: 'lightgray',
        justifyContent: 'center',
        alignItems: 'center',
    }
})
export default MainPage
