import React, { useState } from 'react'
import { Text, View, StyleSheet, TextInput, TouchableOpacity, Image } from 'react-native'


import {create} from '../../functions/database/usuarios'

const Register = ({navigation}) => {
    const [botao, setBotao] = useState()
    const [email, setEmail] = useState()
    const [usuario, setUsuario] = useState()
    const [senha1, setSenha1] = useState()
    const [senha2, setSenha2] = useState()


    async function validation() {
        try {
            if (email && usuario && senha1 && senha2) {
                if (senha1 != senha2) {
                    alert('Senhas diferentes')
                } else {
                    await create({email:email, senha:senha1, usuario:usuario}).then(()=>{
                        alert(`Criado email: ${email}  usuario: ${usuario} `)
                        navigation.navigate("MainPage")
                    }).catch(err=>{
                        alert(`Erro durante a criação de conta`)
                        navigation.navigate("Register")
                    })
                }
            } else {
                alert('Preencher todos os campos')
            }
        } catch (error) {
            throw new Error(`Erro: ${error}`)
        }
    }

    return (
        <View style={style.container}>
            <Image style={style.image} source={{ uri: "https://repository-images.githubusercontent.com/78664391/f7e46780-6bf6-11eb-999f-8212c69d76bc" }} />
            <Text>E-mail</Text>
            <TextInput style={style.input} onChangeText={setEmail} ></TextInput>
            <Text>Usuário</Text>
            <TextInput style={style.input} onChangeText={setUsuario}></TextInput>
            <Text>Senha</Text>
            <TextInput style={style.input} onChangeText={setSenha1}></TextInput>
            <Text>Senha</Text>
            <TextInput style={style.input} onChangeText={setSenha2}></TextInput>
            <TouchableOpacity style={style.btn} onPress={() => validation()} ><Text style={{ color: "white" }}>Register</Text></TouchableOpacity>
            <TouchableOpacity style={style.btn} onPress={() => navigation.navigate("Login")} ><Text style={{ color: "white" }}>Login</Text></TouchableOpacity>
        </View>
    )
}

const style = StyleSheet.create({
    btn: {
        backgroundColor: "red",
        color: "#FFFFFF",
        alignItems: "center",
        borderRadius: 10,
        borderWidth: 1,
        borderStyle: "solid",
        borderColor: "black",
        marginTop:20,
        width:100
    },
    input: {
        textAlign:"center",
        width:100,
        borderWidth: 1,
        borderStyle:"solid",
        borderColor:"black",
        borderRadius: "5%",
        borderRadius: 10
    },
    image: {
        alignSelf: "center",
        width: 120,
        height: 100
    },
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
      }
})

export default Register
