import React, { useState } from 'react'
import { Text, View, StyleSheet, TextInput, TouchableOpacity, Image } from 'react-native'
import { Login } from '../'
import GraphqlService from '../../functions/axios'
const service = new GraphqlService()

const Register = ({ verifyAccessToken }) => {
    const [botao, setBotao] = useState()
    const [email, setEmail] = useState()
    const [usuario, setUsuario] = useState()
    const [senha, setSenha] = useState()




    function validation() {
        if (email && usuario) {
            console.log("request")
            service.post(JSON.stringify(email), JSON.stringify(usuario), JSON.stringify(senha))

            alert(`email: ${email}  usuario ${usuario} senha: ${senha}`)
        } else {
            alert('Preencher todos os campos')
        }

    }

    function validationToLogin() {
        verifyAccessToken()

    }
    return (
        <View>
            <Image style={style.image} source={{ uri: "https://repository-images.githubusercontent.com/78664391/f7e46780-6bf6-11eb-999f-8212c69d76bc" }} />
            <Text>E-mail</Text>
            <TextInput style={style.input} onChangeText={setEmail} ></TextInput>
            <Text>Usuário</Text>
            <TextInput style={style.input} onChangeText={setUsuario}></TextInput>
            <Text>Senha</Text>
            <TextInput style={style.input} onChangeText={setSenha}></TextInput>
            <Text>Senha</Text>
            <TextInput style={style.input} onChangeText={setSenha}></TextInput>
            <View style={{ flexDirection: "row", width: "100%" }}>
                <TouchableOpacity style={style.btn} onPress={() => validation()} >Register</TouchableOpacity>
                <TouchableOpacity style={style.btn} onPress={() => validationToLogin()} >Login</TouchableOpacity>

            </View>

        </View>

    )
}

const style = StyleSheet.create({
    btn: {
        width: "50%",
        backgroundColor: "red",
        color: "#FFFFFF",
        fontFamily: "arial",
        textAlign: "center",
        marginTop: 10,
        borderRadius: 10,
        border: "1px solid black",
        margin: 1
    },
    input: {
        border: "1px solid black",
        borderRadius: "5%",
        borderRadius: 10
    },
    image: {
        alignSelf: "center",
        width: 120,
        height: 100
    }
})

export default Register
