import 'react-native-gesture-handler';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, TouchableOpacity } from 'react-native';
import { Register } from './src/pages';
import { Login } from './src/pages'
import { NavigationContainer, StackActions } from '@react-navigation/native'
import React, { useState } from 'react'
import { createStackNavigator } from '@react-navigation/stack';
const Stack = createStackNavigator();

function MainPage({navigation}){
return (
  <Text>PATO NO TUCUPI</Text>
)
}

function Home({navigation}) {
  const [hasToken, setHasToken] = useState(false)

  function verifyAccessToken() {

    setHasToken((estadoAnterior) => {
      return !estadoAnterior
    })
  }
  return (
    <View style={styles.container}>
      {!hasToken ? (<Register verifyAccessToken={verifyAccessToken} />) : (<Login verifyAccessToken={verifyAccessToken} />)}
      <StatusBar style="auto" />
      <TouchableOpacity onPress={()=>navigation.navigate("MainPage")}><Text>Main Page</Text></TouchableOpacity>
    </View>
  )
}

export default function App() {


  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName='Home'>
        <Stack.Screen name='Home' component={Home} />
        <Stack.Screen  name='MainPage' component={MainPage}/>
      </Stack.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
