import knex from 'knex'
import config from '../knexfile'
const db = knex(config.development)

export class Database {

    async addToBase(values) {
        return db('dadosUsuarios')
            .insert({
                email: values.email,
                usuario: values.usuario,
                senha: values.senha
            })

    }
    
    async getUser(email) {
        return db.select("*")
            .from("dadosUsuarios")
            .where({ email: email })
    }

    async getUsers() {
        return db.select("*")
            .from("dadosUsuarios")
    }
}
