import React, { useState } from 'react'
import { Text, View, StyleSheet, TextInput, TouchableOpacity, Image } from 'react-native'
import { Login } from '../'
import GraphqlService from '../../functions/axios'
const service = new GraphqlService()

const Register = ({navigator}) => {
    const [botao, setBotao] = useState()
    const [email, setEmail] = useState()
    const [usuario, setUsuario] = useState()
    const [senha1, setSenha1] = useState()
    const [senha2, setSenha2] = useState()


    function validation() {
        try {
            if (email && usuario && senha1 && senha2) {
                if (senha1 != senha2) {
                    alert('Senhas diferentes')
                } else {
                    console.log("request")
                    const response = service.post(JSON.stringify(email), JSON.stringify(usuario), JSON.stringify(senha1))
                    if (response) {
                        alert(`email: ${email}  usuario: ${usuario} senha: ${senha1}`)
                        
                    } else {
                        alert(`Erro não programado`)
                    }
                }
            } else {
                alert('Preencher todos os campos')
            }
        } catch (error) {
            throw new Error(`Erro: ${error}`)
        }
    }

    function validationToLogin() {
        verifyAccessToken()

    }
    return (
        <View>
            <Image style={style.image} source={{ uri: "https://repository-images.githubusercontent.com/78664391/f7e46780-6bf6-11eb-999f-8212c69d76bc" }} />
            <Text>E-mail</Text>
            <TextInput style={style.input} onChangeText={setEmail} ></TextInput>
            <Text>Usuário</Text>
            <TextInput style={style.input} onChangeText={setUsuario}></TextInput>
            <Text>Senha</Text>
            <TextInput style={style.input} onChangeText={setSenha1}></TextInput>
            <Text>Senha</Text>
            <TextInput style={style.input} onChangeText={setSenha2}></TextInput>
            <View style={{ flexDirection: "row", width: "100%" }}>
                <TouchableOpacity style={style.btn} onPress={() => validation()} ><Text style={{ color: "white" }}>Register</Text></TouchableOpacity>
                <TouchableOpacity style={style.btn} onPress={() => navigator.navigate("Login")} ><Text style={{ color: "white" }}>Login</Text></TouchableOpacity>

            </View>
        </View>

    )
}

const style = StyleSheet.create({
    btn: {
        backgroundColor: "red",
        color: "#FFFFFF",
        textAlign: "center",
        marginTop: 10,
        borderRadius: 10,
        borderWidth: 1,
        borderStyle:"solid",
        borderColor:"black",
        margin: 10
    },
    input: {
        borderWidth: 1,
        borderStyle:"solid",
        borderColor:"black",
        borderRadius: "5%",
        borderRadius: 10
    },
    image: {
        alignSelf: "center",
        width: 120,
        height: 100
    }
})

export default Register
