import React, { useEffect, useState } from 'react'
import { Text, View, Button, FlatList, StyleSheet, ScrollView } from 'react-native'
import GraphqlService from '../../functions/axios'
const service = new GraphqlService()

const MainPage = ({ navigation }) => {
    const [fetchData, setFetchData] = useState([]);

    async function showAllUsers() {
        let usersList = await service.getAllUsers()
        setFetchData(usersList)
         
    }
    // useEffect(()=>{console.log(fetchData)})
    return (
        <ScrollView>
            <Button onPress={() => showAllUsers()} title="Usuarios Cadastrados Na Base"></Button>
            {fetchData.length>0 && 
             fetchData.map((item,index)=>{
                return <Text style={style.items} key={index}>{item.nome}</Text>
             })
             }

        </ScrollView>

    )
}
const style = StyleSheet.create({
    items:{
        textAlign:"center"
    }
})
export default MainPage
